import React, { useEffect } from 'react';
import { Input, Typography, Form, Checkbox, Row, Col, Image } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import FormWrap from '../../components/FormWrap';
import { useParams } from 'react-router';
import WrappedSpinner from '../../components/WrappedSpinner';
import { getItemById } from '../../features/Items/ItemsActions';
import { Link } from 'react-router-dom';

const Item = () => {
  const { item, isLoading } = useSelector(({ items }) => items);
  const dispatch = useDispatch();
  const { id } = useParams();

  useEffect(() => {
    dispatch(getItemById(id));
  }, [dispatch, id]);

  const formFields = item
    ? [
        {
          label: 'Id',
          component: <Input value={item._id} disabled />,
        },
        {
          label: 'Name',
          component: <Input value={item.title} disabled />,
        },
        {
          label: 'Brand',
          component: <Link to={`/brands/${item.brand._id}`}>{item.brand.name}</Link>,
        },
        {
          label: 'Category',
          component: <Link to={`/categories/${item.category._id}`}>{item.category.name}</Link>,
        },
        {
          label: 'Color',
          component: <Input value={item.color} disabled />,
        },
        {
          label: 'Price',
          component: <Input value={`${item.price} $`} disabled />,
        },
        {
          label: 'Description',
          component: <Input.TextArea value={`${item.description} $`} disabled />,
        },
        {
          label: 'Available',
          component: <Checkbox checked={`${item.available} $`} disabled />,
        },
        {
          label: 'Image URL',
          component: (
            <a href={item.imgUrl} target="_blank" rel="noreferrer">
              {item.imgUrl}
            </a>
          ),
        },
      ]
    : [];

  if (isLoading) {
    return <WrappedSpinner />;
  }

  return (
    <>
      <Typography.Title className="mt-1" level={1}>
        Item
      </Typography.Title>
      <FormWrap>
        <Row gutter={16}>
          <Col span={12}>
            <Form name="basic" labelCol={{ span: 4 }} initialValues={{ remember: true }} autoComplete="off">
              {formFields.map((f) => (
                <Form.Item key={`item-${f.label}`} label={f.label}>
                  {f.component}
                </Form.Item>
              ))}
            </Form>
          </Col>
          <Col span={12}>
            <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
              <Image
                src={item.imgUrl || 'error'}
                fallback="data:image/png;base64,"
              />
            </div>
          </Col>
        </Row>
      </FormWrap>
    </>
  );
};

export default Item;
